import React, { useState } from "react";
import UserInput from "./components/Users/UserInput";
import UserList from "./components/Users/UserList";

function App() {
  const [userNames, setUserNames] = useState([
    { name: "Max (31 years old)", id: "u1" },
  ]);

  const handleAddUser = ({enteredName, enteredAge}) => {
    setUserNames((prevUsers) => {
      const updatedUsers = [...prevUsers];
      updatedUsers.unshift({ name: `${enteredText}${" years old"}`, id: Math.random().toString() });
      return updatedUsers;
    });
  };

  return (
    <div>
      <section>
        <UserInput onAddUser={handleAddUser} />
      </section>
      <section>
        <UserList users={userNames} />
      </section>
    </div>
  );
}

export default App;
